console.log("Load script.js");var app={},queryarr=document.querySelector("query");app.global={init:function(){console.log("load global functions"),app.global.loadHeader()},loadHeader:function(){console.log("loadHeader()")},classCheck:function(e){}},app.global.init();for(var query=document.getElementsByClassName("query"),i=0;i<query.length;i++)query[i].addEventListener("click",function(){if(this.classList.contains("active"))this.classList.remove("active");else{for(var e=0;e<query.length;e++)query[e].classList.remove("active");this.classList.add("active")}});